#!/usr/bin/env bash

# This is a simple greeting function
# It says hello to the world
hello_world() {
  echo "Hello, World!"
}

# Multi-line comment
#
# with indentation
#   - First point
#   - Second point
#
# End of comment
formatted_function() {
  echo "Formatted"
}

# Single line comment
single_line() {
  echo "One liner"
}

no_comment_function() {
  echo "No comment"
}

# This comment is not attached
# Because there's a blank line above

detached_comment() {
  echo "Detached"
}

# Comment with blank lines in between
#
# Still part of the same comment block
blank_lines_in_comment() {
  echo "Blank lines"
}

# First function comment
first_func() {
  echo "First"
}
# Second function comment
second_func() {
  echo "Second"
}
